function updatePercentage(){var e=mousePos/screenHeight*100;e=Math.min(Math.max(e,marginTop),marginBottom),percentage=(50*percentage+e)/51,h1.style.height=percentage+"%",h2.style.height=100-percentage+"%",requestAnimationFrame(updatePercentage)}function calculateMargins(){screenHeight=window.innerHeight,marginTop=h1span.offsetHeight/screenHeight*100*1.5,marginBottom=100-h2span.offsetHeight/screenHeight*100*1.5}var getUrlVars=function(){for(var e,t=[],n=window.location.href.slice(window.location.href.indexOf("?")+1).split("&"),r=0;r<n.length;r++)e=n[r].split("="),t.push(e[0]),t[e[0]]=e[1];return t},urlVars=getUrlVars();if("undefined"!=typeof urlVars.title)var coverTitle=atob(urlVars.title);else var coverTitle="Title was not passed";if("undefined"!=typeof urlVars.subtitle)var coverSubtitle=atob(urlVars.subtitle);else var coverSubtitle="Subtitle was not passed";var h1=document.querySelector("h1"),h2=document.querySelector("h2"),h1span=document.querySelector("h1 span"),h2span=document.querySelector("h2 span");h1span.innerText=coverTitle,h2span.innerText=coverSubtitle;var screenHeight=window.innerHeight,percentage=50,mousePos=0,marginTop=8,marginBottom=95;window.onmousemove=function(e){mousePos=e.clientY},window.onresize=calculateMargins,calculateMargins(),requestAnimationFrame(updatePercentage);