var getUrlVars=function(){for(var e,t=[],r=window.location.href.slice(window.location.href.indexOf("?")+1).split("&"),i=0;i<r.length;i++)e=r[i].split("="),t.push(e[0]),t[e[0]]=e[1];return t},urlVars=getUrlVars();if("undefined"!=typeof urlVars.title)var coverTitle=atob(urlVars.title);else var coverTitle="Title was not passed";if("undefined"!=typeof urlVars.subtitle)var coverSubtitle=atob(urlVars.subtitle);else var coverSubtitle="Subtitle was not passed";var h1=document.querySelector("h1"),h2=document.querySelector("h2");h1.innerText=coverTitle,h2.innerText=coverSubtitle;var screenHeight=window.innerHeight;window.onmousemove=function(e){var t=e.clientY/screenHeight*100;t=Math.min(Math.max(t,8),95),h1.style.height=t+"%",h2.style.height=100-t+"%"};